import React , {useEffect, useState}from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import AccountTemplate from './Template/accountTemplate';
import AddAccountTemplate from './Template/addAccountTemplate';
import Typography from '@material-ui/core/Typography';
import axios from "axios";
import {
  Button,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Paper,
} from "@material-ui/core";
const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const api = {
  urlGetUser: "https://dev.geo.sdis67.com/api/v1/public/allUsers",
  urlGetCities: "https://dev.geo.sdis67.com/api/v1/app/erp/cities"
};
export default function DashBoard(props) {
  const classes = useStyles();
  const [selectedRow, setSelectedRow] = useState(null);
  const [open, setOpen] = useState(false);
  const [apiData, setApiData]= useState(null);
  const [cities, setCities]= useState(null);
  const [openAddAccount, setOpenAddAccount] = React.useState(false);
  const [identity, setIdentity] = React.useState({
    userName: "",
    firstName: "",
    lastName: "",
    email: "",
    registrationNumber: "",
    cities: null,
    password: null,
  });
  const handleChangeIdentity = ( event) => {
    if(event.target.name === "userName") {
      identity.userName = event.target.value;
    }else if (event.target.name === "firstName"){
      identity.firstName = event.target.value;
    }else if(event.target.name === "lastName") {
      identity.lastName = event.target.value;
    }else if(event.target.name === "email"){
      identity.email= event.target.value;
    }else if (event.target.name === "registrationNumber"){
      identity.registrationNumber = event.target.value;
    }else if (event.target.name === "password"){
      identity.password = event.target.value;
    }
    setIdentity(identity);
  };
  const handleChangeCities =(event, value, reason) => {
    identity.cities = value;
    setIdentity(identity);

  }
  const getAPIData = async () => {
    const res = await axios.get(`${api.urlGetUser}`);
    setApiData(res.data.data)
  };
  useEffect(() => {
    console.log("getapi");
    getAPIData();
  }, []);
  const getCities = async () => {
    const res = await axios.get(`${api.urlGetCities}`);
    setCities(res.data.data.cities);
  }
  useEffect(() => {
    console.log("getCities");
    getCities();
  }, []);
  console.warn("apirequest", apiData);
  const handleClickSetDialog = (e, data) => {
    setOpen(true);
    setSelectedRow(data);
  };
  const handleClickOpenAddAccount = () => {
    setOpenAddAccount(true);
  };
  const handleClose = () => {
    setOpen(false);
    setOpenAddAccount(false);
  };
  const addAcount = (
    <AddAccountTemplate city={cities} changeIdentity={handleChangeIdentity} changeCities={handleChangeCities}/>
  );
  console.warn("identity", identity);
  const postAccount = async ()=> {
    console.log("postAccount")
    await axios.post('https://dev.geo.sdis67.com/api/v1/public/user', identity).then(res => {
      console.log(res);})
  }
  return (
    <div>
      {apiData &&
        apiData.map((data) => {
          return (
            <div key={data.id}>
              <Button
                onClick={(e) => {
                handleClickSetDialog(e, data);
                }}
              >
                <Paper variant="outlined">
                  <List dense>
                    <ListItem >
                      <ListItemAvatar>
                        <Avatar>Nom</Avatar>
                      </ListItemAvatar>
                      <ListItemText primary={`Nom:  ${data.lastName}  ${data.firstName}`} />
                    </ListItem>
                  </List>
                </Paper>
              </Button>
              <br/>
            </div>
          );
      })}
      {selectedRow && (
        <div>
          <AccountTemplate
            selectedRow={selectedRow}
            open={open}
            onClickClose={handleClose}
            city={cities}
            setOpen={setOpen}

          />
        </div>
      )}
      <Button variant="outlined" color="primary" onClick={handleClickOpenAddAccount}>
        Ajouter un profil
      </Button>
      <Dialog fullScreen open={openAddAccount} onClose={handleClose} TransitionComponent={Transition}>
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={handleClose} aria-label="close">
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Ajout d'un profil
            </Typography>
            <Button autoFocus color="inherit" onClick={postAccount} edge="endpoint">
              Sauvegarder
            </Button>
          </Toolbar>
        </AppBar>
        {addAcount}
      </Dialog>

    </div>
  );
}
